class Mask{constructor(element){this.element_=element}mask(mask,reverse){reverse?this.reverseMask(mask):this.normalMask(mask)}normalMask(mask){this.element_.value=this.getMaskered(mask,this.element_.value)}reverseMask(mask){mask=this.reverseString(mask);let value=this.reverseString(this.element_.value),result=this.getMaskered(mask,value);this.element_.value=this.reverseString(result)}getMaskered(mask,string){string=string.replace(/[\W]/g,"");let ret="";for(let i=0,j=0;i<=mask.length-1&&null!=string[j];i++)ret+="#"==mask[i]?string[j++]:mask[i];return ret}reverseString(string){return string.split("").reverse().join("")}}